mapper.select:
SELECT COUNT(*)
FROM FORBIDDEN 
WHERE FORBIDDEN_WORD =  #{fword}

SELECT *
FROM BUYLIST


WHERE USER_BUY_DATE BETWEEN TO_CHAR(#{preday})
AND TO_DATE(#{postday},'YYYY-MM-DD')+0.99999

AND USER_BUY_MASTER = #{searchBox}
ORDER BY USER_BUY_NUM DESC


AND USER_EMAIL = #{searchBox}
ORDER BY USER_BUY_NUM DESC


AND USER_BUY_TITLE = #{searchBox}
ORDER BY USER_BUY_NUM DESC

SELECT COUNT (*)
FROM BUYLIST


WHERE USER_BUY_DATE BETWEEN TO_CHAR(#{preday})
AND TO_DATE(#{postday},'YYYY-MM-DD')+0.99999

AND USER_BUY_MASTER = #{searchBox}
ORDER BY USER_BUY_NUM DESC


AND USER_EMAIL = #{searchBox}
ORDER BY USER_BUY_NUM DESC


AND USER_BUY_TITLE = #{searchBox}
ORDER BY USER_BUY_NUM DESC
