mapper.select:
SELECT COUNT(*)
FROM NEEZ_USER
WHERE USER_PHONE = #{phone}

SELECT COUNT(*)
FROM NEEZ_USER
WHERE USER_EMAIL = #{email}

mapper.update:
	UPDATE NEEZ_USER
 	SET USER_PHONE = #{phone}
 	WHERE USER_EMAIL = #{email}

